import React, { Component } from 'react';

import WcList from './WcList';
import WcItem from './WcItem';

class Wapp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            classes: [],
            currentClass: null,
            classesAreLoading: true
        };
    }

    componentDidMount() {
        fetch('http://localhost:3001/classes', { headers: { 'Content-Type': 'application/json' } })
            .then(r => r.json())
            .then(classes => {
                this.setState({ classes, classesAreLoading: false });
            });
    }

    addNewClass = newClass => {
        const { classes } = this.state;
        classes.push(newClass);
        this.setState({ classes });
    };

    selectClass = thatClass => {
        this.setState({ currentClass: thatClass });
    };

    render() {
        const { classes, coachIsAddingClass, currentClass } = this.state;
        return (
            <div className="app">
                <header className="app-header">
                    <h1 className="app-header__title">HYFBE GitHub Dashboard</h1>
                </header>
                <main className="content">
                    <WcList
                        classes={classes}
                        selectClass={this.selectClass}
                        addNewClass={this.addNewClass}
                        classesAreLoading={this.state.classesAreLoading}
                    />
                    <WcItem classItem={currentClass} />
                </main>
            </div>
        );
    }
}

export default Wapp;
